def printc(str_):

    if 'unittest' in sys.argv[0]:
        print(f"{__name__=} {sys.argv=}")
        print("unsupported chars unittest")
        return
    try:
        print(Style.GREEN(str(str_, 'Utf-8')))
    except TypeError:
        try:
            print(Style.GREEN(str(str_, 'ISO-88591')))
        except TypeError:
            print(str_)

