0010 !       LS
0020 ! *************************
0030 ! * THIS BINARY IMPLEMENTS 
0040 ! * A 'PRINT COL' COMMAND  
0050 ! * THAT ALLOWS A SINGLE   
0060 ! * LINE OF GRAPHICS TO BE 
0070 ! * OUTPUT TO THE PRINTER. 
0080 ! * THE FORMAT OF THE      
0090 ! * STATEMENT IS :         
0100 ! *   PRINT COL<$ EXPRESS.>
0110 ! *************************
0120 CRTPOF DAD  35703
0130 CRTPUP DAD  35716
0140 PRSTS  DAD  177416
0150 PRDATA DAD  177417
0160 GETRDY DAD  76435
0170 GRHOME DAD  75746
0180 GRINIT DAD  76113
0190 ERROR+ DAD  6611
0200 STREX+ DAD  13623
0201 PRMLEN DAD  177414
0202 GINTEN DAD  177400
0203 GINTDS DAD  177401
0204 GR.OUT DAD  75760
0206 ! *************************
0210        NAM  PRCOL 
0220        DEF  RUNTIM
0230        DEF  TOKS  
0240        DEF  PARSES
0250        DEF  ERMSG 
0260 RUNTIM DEF  INIT  
0270 PARSES DEF  PCOL. 
0280        DEF  PCOLPR
0290 ERMSG  OCT  377,377
0300 TOKS   ASC  10,PRINT COL
0310        OCT  314
0320        OCT  377
0330 ! ************************
0340 PCOLPR PUBD R43,+R6
0350        JSB  =STREX+
0360        POBD R47,-R6
0370        JEN  PARSOK
0380        JSB  =ERROR+
0390        OCT  82D
0400 PARSOK LDB  R45,=371
0410        PUMD R45,+R12
0420        RTN  
0422 ! ************************
0424 OUTPAP JSB  =GR.OUT
0426        RTN  
0430 ! ************************
0440        OCT  241
0450 PCOL.  JSB  =CRTPOF
0460        JSB  =GRINIT
0470        JEN  INIT  
0480        JSB  =GRHOME
0484        CLM  R22
0490        BIN  
0500        LDB  R24,=30
0510        POMD R26,-R12
0520        POMD R36,-R12
0530        JZR  NOT30 
0532        SBM  R36,=10,0
0534        JNC  NOT30 
0536        JMP  LOP+  
0540 PRTLOP SBM  R36,=10,0
0550        JNC  <8    
0560 LOP+   POMD R70,+R26
0562 PRINT  JSB  =GETRDY
0564        JPS  OUTPAP
0566        LDB  R#,=10
0567        STBD R#,=GINTDS
0568        STBD R#,=PRMLEN
0570        JSB  =GETRDY
0580        STMD R70,=PRDATA
0590        DCB  R24
0600        JNZ  PRTLOP
0602        JSB  =GRHOME
0604        STBD R#,=GINTEN
0610        LDB  R24,=30
0620        JMP  PRTLOP
0630 <8     CMB  R24,=30
0640        JNZ  NOT30 
0650        CLB  R24
0660 NOT30  TSB  R24
0670        JZR  PRTDON
0680        LDB  R0,=70
0690        ADM  R36,=10,0
0700        JZR  CLM-R*
0710 POP-R* POBD R*,+R26
0720        ICB  R0
0730        DCB  R36
0740        JNZ  POP-R*
0750 CLM-R* CLM  R*
0760        JMP  PRINT 
0770 PRTDON JSB  =GETRDY
0780        STBD R24,=PRSTS 
0790        JSB  =CRTPUP
0800 INIT   RTN  
0990        FIN  
